  const [activeTab, setActiveTab] = useState("clubs"); // Aktif sekme
  const [clubs, setClubs] = useState([]);
  const [players, setPlayers] = useState([]);
  const [users, setUsers] = useState([]);

  const [newClub, setNewClub] = useState({
    clubName: "",
    foundationDate: "",
    leagueId: "",
    reputation: "",
    presidentName: "",
    managerId: "",
  });

  const [newPlayer, setNewPlayer] = useState({
    playerName: "",
    age: "",
    position: "",
    clubId: "",
  });

  const [newUser, setNewUser] = useState({
    username: "",
    email: "",
    role: "",
  });

  // Veri çeken fonksiyonlar
  const fetchClubs = async () => {
    try {
      const response = await axios.get("http://localhost:8080/api/v1/clubs");
      setClubs(response.data);
    } catch (error) {
      console.error("Error fetching clubs:", error);
    }
  };

  const fetchPlayers = async () => {
    try {
      const response = await axios.get("http://localhost:8080/api/v1/players");
      setPlayers(response.data);
    } catch (error) {
      console.error("Error fetching players:", error);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:8080/api/v1/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

