              placeholder="Username"
              value={newUser.username}
              onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
            />
            <button onClick={handleAddUser}>Add User</button>
          </div>
          <ul>
            {users.map((user) => (
              <li key={user.userId}>
                {user.username}
                <button onClick={() => handleDeleteUser(user.userId)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

