import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Players.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);
  const [favorites, setFavorites] = useState([]);
  const [filters, setFilters] = useState({
      minAge: null,
      maxAge: null,
      countryId: null,
      clubId: null,
      leagueId: null,
      positionIds: [],
      minMarketValue: null,
      maxMarketValue: null,
      minCa: null,
      maxCa: null,
      minPa: null,
      maxPa: null,
      name: '',
      club: '',
      nationality: '',
      league: '',
  });
  const navigate = useNavigate();
  const playersPerPage = 30;
  const paginationGroupSize = 10;
  const taken = localStorage.getItem("userId");

  useEffect(() => {
    const fetchPlayers = async () => {
        setLoading(true);
        try {
            const response = await axios.get("http://localhost:8080/api/v1/football-players");
            const transformedData = response.data.map((player) => ({
                id: player.playerId,
                name: player.name,
                club: player.clubName,
                position: player.positions,
                salary: player.salary,
                ca: player.ca,
                pa: player.pa,
                age: player.age,
                clubId: player.clubId,
            }));
            setPlayers(transformedData);
        } catch (error) {
            console.error("Error fetching players:", error);
        } finally {
            setLoading(false);
        }
    };
    fetchPlayers();
}, []);


  useEffect(() => {
    const fetchFilteredPlayers = async () => {
        setLoading(true);
        try {
            const params = new URLSearchParams();
            for (const key in filters) {
                if (filters[key] !== null && filters[key] !== undefined && filters[key] !== '') {
                    if (Array.isArray(filters[key])) {
                        filters[key].forEach(item => params.append(key, item));
                    } else {
                        params.append(key, filters[key]);
                    }
                }
            }


            const response = await axios.get(`http://localhost:8080/api/v1/football-players/filter?${params.toString()}`);
            const transformedData = response.data.map((player) => ({
                id: player.playerId,
                name: player.name,
                club: player.clubName,
                position: player.positions,
                salary: player.salary,
                ca: player.ca,
                pa: player.pa,
                age: player.age,
                clubId: player.clubId,
            }));
            setPlayers(transformedData);
        } catch (error) {
          console.error("Error fetching filtered players:", error);
        } finally {
           setLoading(false);
        }
    };


    if (Object.values(filters).some(value => value !== null && value !== undefined && value !== '')) {
      fetchFilteredPlayers();
    }else{
         const fetchPlayers = async () => {
        setLoading(true);
        try {
            const response = await axios.get("http://localhost:8080/api/v1/football-players");
            const transformedData = response.data.map((player) => ({
                id: player.playerId,
                name: player.name,
                club: player.clubName,
                position: player.positions,
                salary: player.salary,
                ca: player.ca,
                pa: player.pa,
                age: player.age,
                clubId: player.clubId,
            }));
            setPlayers(transformedData);
        } catch (error) {
            console.error("Error fetching players:", error);
        } finally {
            setLoading(false);
        }
    };
      fetchPlayers();

    }
  }, [filters]);
  const indexOfLastPlayer = currentPage * playersPerPage;
  const indexOfFirstPlayer = indexOfLastPlayer - playersPerPage;
  const currentPlayers = players.slice(indexOfFirstPlayer, indexOfLastPlayer);


  const handleAddToFavorites = async (player) => {
    if (taken === null) {
      navigate("/login");
      return;
    }
    try {
      const userId = Number(localStorage.getItem("userId"));
      if (!favorites.some((fav) => fav.id === player.id)) {
        setFavorites([...favorites, player]);
        const response = await fetch(
          `http://localhost:8080/api/v1/favourites?userId=${userId}&playerId=${player.id}`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
          }
        );
        if (response.ok) {
          console.log("Favoriye ekleme başarılı!");
        } else {
          console.error("Favoriye ekleme hatası:", await response.text());
        }
      }
    } catch (error) {
      console.error("Favoriye eklerken bir hata oluştu:", error);
    }
  };
  const handleFilterChange = (e) => {
    const { id, value } = e.target;
    setFilters(prevFilters => ({
      ...prevFilters,
      [id]: value === '' ? null : value,
    }));
  };
    const handlePositionChange = (e) => {
        const { value, checked } = e.target;
        setFilters(prevFilters => {
            let updatedPositions = [...prevFilters.positionIds];
            if (checked) {
                updatedPositions.push(Number(value));
            } else {
                updatedPositions = updatedPositions.filter(id => id !== Number(value));
            }
             return {
                ...prevFilters,
                positionIds: updatedPositions,
            };
        });
    };
  const totalPages = Math.ceil(players.length / playersPerPage);

  const paginate = (pageNumber) => {
    if (pageNumber > 0 && pageNumber <= totalPages) setCurrentPage(pageNumber);
  };

  const currentPaginationGroup = Math.floor((currentPage - 1) / paginationGroupSize);
  const startPage = currentPaginationGroup * paginationGroupSize + 1;
  const endPage = Math.min(startPage + paginationGroupSize - 1, totalPages);

  const goToPreviousGroup = () => {
    const previousGroupStart = startPage - paginationGroupSize;
    if (previousGroupStart > 0) {
      setCurrentPage(previousGroupStart);
    }
  };

  const goToNextGroup = () => {
    const nextGroupStart = startPage + paginationGroupSize;
    if (nextGroupStart <= totalPages) {
      setCurrentPage(nextGroupStart);
    }
  };

  if (loading) {
    return <div className="loading">Loading players...</div>;
  }
  return (
    <div className="players-page">
      <aside className="players-sidebar">
        <h2 style={{ color: "black" }}>Filters</h2>
        <div className="filter-group">
          <label htmlFor="name">Name</label>
          <input type="text" id="name" placeholder="Name" value={filters.name} onChange={handleFilterChange}/>
        </div>
        <div className="filter-group">
          <label htmlFor="club">Club</label>
          <input type="text" id="club" placeholder="Club" value={filters.club} onChange={handleFilterChange}/>
        </div>
        <div className="filter-group">
          <label htmlFor="nationality">Nationality</label>
          <input type="text" id="nationality" placeholder="Nationality" value={filters.nationality} onChange={handleFilterChange}/>
        </div>
         <div className="filter-group">
          <label htmlFor="league">League</label>
          <input type="text" id="league" placeholder="League" value={filters.league} onChange={handleFilterChange}/>
        </div>

          <div className="filter-group">
            <label htmlFor="minAge">Min Age</label>
            <input type="number" id="minAge"  onChange={handleFilterChange} />
        </div>
        <div className="filter-group">
            <label htmlFor="maxAge">Max Age</label>
            <input type="number" id="maxAge"  onChange={handleFilterChange} />
        </div>
          <div className="filter-group">
            <label htmlFor="minMarketValue">Min Market Value</label>
            <input type="number" id="minMarketValue"  onChange={handleFilterChange}/>
        </div>
        <div className="filter-group">
            <label htmlFor="maxMarketValue">Max Market Value</label>
            <input type="number" id="maxMarketValue"   onChange={handleFilterChange} />
        </div>
        <div className="filter-group">
            <label htmlFor="minCa">Min CA</label>
            <input type="number" id="minCa"   onChange={handleFilterChange}/>
        </div>
        <div className="filter-group">
            <label htmlFor="maxCa">Max CA</label>
            <input type="number" id="maxCa"  onChange={handleFilterChange} />
        </div>
         <div className="filter-group">
            <label htmlFor="minPa">Min PA</label>
            <input type="number" id="minPa"   onChange={handleFilterChange} />
        </div>
        <div className="filter-group">
            <label htmlFor="maxPa">Max PA</label>
            <input type="number" id="maxPa"  onChange={handleFilterChange} />
        </div>

        <div className="section-title"><strong>Position</strong></div>
        <div className="checkbox-group">
            <tr>
                <label>
                    <input type="checkbox" value="1" onChange={handlePositionChange} /> GK
                </label>
            </tr>
            <tr>
                <label>
                    <input type="checkbox" value="2" onChange={handlePositionChange} /> DL
                </label>
            </tr>
            <tr>
                <label>
                    <input type="checkbox" value="3" onChange={handlePositionChange} /> DC
                </label>
            </tr>
            <tr>
                <label>
                    <input type="checkbox" value="4" onChange={handlePositionChange} /> DR
                </label>
            </tr>
            <tr>
                <label>
                    <input type="checkbox" value="5" onChange={handlePositionChange} /> WBL
                </label>
            </tr>
            <tr>
                <label>
                    <input type="checkbox" value="6" onChange={handlePositionChange} /> WBR
                </label>
            </tr>
            <tr>
                <label>
                    <input type="checkbox" value="7" onChange={handlePositionChange} /> ML
                </label>
            </tr>
            <tr>
                <label>
                    <input type="checkbox" value="8" onChange={handlePositionChange} /> MC
                </label>
            </tr>
             <tr>
                <label>
                    <input type="checkbox" value="9" onChange={handlePositionChange} /> MR
                </label>
            </tr>
              <tr>
                <label>
                    <input type="checkbox" value="10" onChange={handlePositionChange} /> FW
                </label>
            </tr>
        </div>
      </aside>
      <main className="players-list">
        <h1>Players</h1>
        <table>
          <thead>
            <tr>
              <th>Add Favourites</th>
              <th>Name</th>
              <th>Club</th>
              <th>Position</th>
              <th>Salary</th>
              <th>CA</th>
              <th>PA</th>
              <th>Age</th>
            </tr>
          </thead>
          <tbody>
            {currentPlayers.map((player) => (
              <tr key={player.id}>
                <td>
                  <button
                    className="favorite-btn"
                    onClick={() => handleAddToFavorites(player)}
                  >
                    ⭐
                  </button>
                </td>
                <td>
                  <Link to={`/players/${player.id}`} className="player-link">
                    {player.name}
                  </Link>
                </td>
                <td>
                  {player.clubId ? (
                    <Link to={`/clubs/${player.clubId}`} className="club-link">
                      {player.club}
                    </Link>
                  ) : (
                    <span>No Club</span>
                  )}
                </td>
                <td>{Array.isArray(player.position) ? player.position.join(", ") : player.position}</td>
                <td>{player.salary}€</td>
                <td>{player.ca}</td>
                <td>{player.pa}</td>
                <td>{player.age}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="pagination">
          <button
            className="pagination-btn"
            onClick={goToPreviousGroup}
            disabled={currentPage <= paginationGroupSize}
          >
           
          </button>

          {Array.from({ length: endPage - startPage + 1 }, (_, i) => {
            const pageNumber = startPage + i;
            return (
              <button
                key={pageNumber}
                className={`pagination-btn ${currentPage === pageNumber ? "active" : ""}`}
                onClick={() => paginate(pageNumber)}
              >
                {pageNumber}
              </button>
            );
          })}

          <button
            className="pagination-btn"
            onClick={goToNextGroup}
            disabled={startPage + paginationGroupSize > totalPages}
          >
           
          </button>
        </div>
      </main>
    </div>
  );
};

export default Players;
