import React, { useState, useEffect } from "react";
import "./Players.css";
import axios from "axios";

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);
  const playersPerPage = 50;
  const paginationGroupSize = 10;

  useEffect(() => {
    // Veritabanından oyuncu verilerini çekiyoruz
    const fetchPlayers = async () => {
      try {
        setLoading(true);
        const response = await axios.get(
          `/api/players?page=${currentPage}&limit=${playersPerPage}`
        ); // Backend'de pagination endpoint
        setPlayers(response.data.players); // players array'i backend'den gelen veri
        setLoading(false);
      } catch (error) {
        console.error("Error fetching players:", error);
        setLoading(false);
      }
    };

    fetchPlayers();
  }, [currentPage]);

  const totalPages = Math.ceil(8000 / playersPerPage); // Toplam sayfa sayısı

  // Sayfa değiştirildiğinde yapılacaklar
  const paginate = (pageNumber) => {
    if (pageNumber > 0 && pageNumber <= totalPages) setCurrentPage(pageNumber);
  };

  const currentPaginationGroup = Math.floor((currentPage - 1) / paginationGroupSize);
  const startPage = currentPaginationGroup * paginationGroupSize + 1;
  const endPage = Math.min(startPage + paginationGroupSize - 1, totalPages);

  const goToPreviousGroup = () => {
    const previousGroupStart = startPage - paginationGroupSize;
    if (previousGroupStart > 0) {
      setCurrentPage(previousGroupStart);
    }
  };

  const goToNextGroup = () => {
    const nextGroupStart = startPage + paginationGroupSize;
    if (nextGroupStart <= totalPages) {
      setCurrentPage(nextGroupStart);
    }
  };

  if (loading) {
    return <div className="loading">Loading players...</div>;
  }

  return (
    <div className="players-page">
      <aside className="players-sidebar">
        <h2>Filters</h2>

        {/* Name Filter */}
        <div className="filter-group">
          <label htmlFor="name">Name</label>
          <input type="text" id="name" placeholder="Enter name" />
        </div>

        {/* Unique ID Filter */}
        <div className="filter-group">
          <label htmlFor="uniqueId">Unique ID</label>
          <input type="text" id="uniqueId" placeholder="Enter unique ID" />
        </div>

        {/* Club Filter */}
        <div className="filter-group">
          <label htmlFor="club">Club</label>
          <input type="text" id="club" placeholder="Enter club" />
        </div>

        {/* Nationality Filter */}
        <div className="filter-group">
          <label htmlFor="nationality">Nationality</label>
          <input type="text" id="nationality" placeholder="Enter nationality" />
        </div>

        {/* League Filter */}
        <div className="filter-group">
          <label htmlFor="league">League</label>
          <input type="text" id="league" placeholder="Enter league" />
        </div>

        {/* Smart Filters */}
        <div className="section-title">Smart Filters</div>
        <div className="checkbox-group">
          <label>
            <input type="checkbox" /> Wonderkids
          </label>
          <label>
            <input type="checkbox" /> Contract expiring
          </label>
          <label>
            <input type="checkbox" /> Free transfers
          </label>
          <label>
            <input type="checkbox" /> Best Bargains
          </label>
          <label>
            <input type="checkbox" /> Dynamic Potential
          </label>
          <label>
            <input type="checkbox" /> Fast paced
          </label>
        </div>

        {/* Line */}
        <div className="section-title">Line</div>
        <div className="checkbox-group">
          <label>
            <input type="checkbox" /> Goalkeepers
          </label>
          <label>
            <input type="checkbox" /> Defenders
          </label>
          <label>
            <input type="checkbox" /> Midfielders
          </label>
          <label>
            <input type="checkbox" /> Attackers
          </label>
        </div>

        {/* Position */}
        <div className="section-title">Position</div>
        <div className="checkbox-group">
          <tr>
          <label>
            <input type="checkbox" /> GK
          </label>
          </tr>
          <tr>
            
          <label>
            <input type="checkbox" /> DL
          </label>
          </tr>
          <label>
            <input type="checkbox" /> DC
          </label>
          <label>
            <input type="checkbox" /> DR
          </label>
          <label>
            <input type="checkbox" /> WBL
          </label>
          <label>
            <input type="checkbox" /> WBR
          </label>
          <label>
            <input type="checkbox" /> DM
          </label>
          <label>
            <input type="checkbox" /> ML
          </label>
          <label>
            <input type="checkbox" /> MC
          </label>
          <label>
            <input type="checkbox" /> MR
          </label>
        </div>
      </aside>


      <main className="players-list">
        <h1>Players</h1>
        <table>
          <thead>
            <tr>
              <th>Photo</th>
              <th>Name</th>
              <th>Club</th>
              <th>Position</th>
              <th>Rating</th>
              <th>Wage</th>
              <th>AC</th>
              <th>PA</th>
              <th>Date of Birth</th>
            </tr>
          </thead>
          <tbody>
            {players.map((player) => (
              <tr key={player.id}>
                <td>
                  {player.image ? (
                    <img
                      src={player.image}
                      alt={player.name}
                      className="player-photo"
                    />
                  ) : (
                    <div className="placeholder-photo">No Image</div>
                  )}
                </td>
                <td>{player.name}</td>
                <td>{player.club}</td>
                <td>{player.position}</td>
                <td>{player.rating}</td>
                <td>{player.wage}</td>
                <td>{player.contractUntil}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="pagination">
          <button
            className="pagination-btn"
            onClick={goToPreviousGroup}
            disabled={currentPage <= paginationGroupSize}
          >
            &lt;&lt;
          </button>

          {Array.from({ length: endPage - startPage + 1 }, (_, i) => {
            const pageNumber = startPage + i;
            return (
              <button
                key={pageNumber}
                className={`pagination-btn ${currentPage === pageNumber ? "active" : ""
                  }`}
                onClick={() => paginate(pageNumber)}
              >
                {pageNumber}
              </button>
            );
          })}

          <button
            className="pagination-btn"
            onClick={goToNextGroup}
            disabled={startPage + paginationGroupSize > totalPages}
          >
            &gt;&gt;
          </button>
        </div>
      </main>
    </div>
  );
};

export default Players;
