import React, { useState } from 'react';
import axios from 'axios';

const Operations = () => {
    const [entityType, setEntityType] = useState('');
    const [operationType, setOperationType] = useState('');
    const [formData, setFormData] = useState({});
    const [responseMessage, setResponseMessage] = useState('');
    const [formVisible, setFormVisible] = useState(false);

    const entityTypes = ['users', 'clubs', 'football-players'];
    const userOperations = ['delete'];
    const otherOperations = ['insert', 'delete', 'update'];

    const handleEntityTypeChange = (event) => {
        const selectedType = event.target.value;
        setEntityType(selectedType);
        setOperationType(''); // Reset operation when entity changes
        setFormData({}); // Reset form data
        setResponseMessage(''); // Reset response message
        setFormVisible(false); // Hide the form
    };

    const handleOperationTypeChange = (event) => {
        const selectedOperation = event.target.value;
        setOperationType(selectedOperation);
        setFormData({}); // Reset form data
        setResponseMessage(''); // Reset response message
        setFormVisible(selectedOperation === 'insert' || selectedOperation === 'update'); // Show form if insert or update
    };

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormData({ ...formData, [name]: value });
    };

    const renderFormFields = () => {
        if (entityType === 'clubs' && (operationType === 'insert' || operationType === 'update')) {
            return (
                <>
                    <input type="text" name="clubName" placeholder="Club Name" onChange={handleInputChange} />
                    <input type="number" name="foundationDate" placeholder="Foundation Date" onChange={handleInputChange} />
                    <input type="number" name="leagueId" placeholder="League ID" onChange={handleInputChange} />
                    <input type="number" name="reputation" placeholder="Reputation" onChange={handleInputChange} />
                    <input type="text" name="presidentName" placeholder="President Name" onChange={handleInputChange} />
                    <input type="number" name="managerId" placeholder="Manager ID" onChange={handleInputChange} />
                    <button onClick={handleSubmit}>Submit</button>
                </>
            );
        } else if (entityType === 'football-players' && (operationType === 'insert' || operationType === 'update')) {
             return (
                 <>
                     <input type="text" name="playerName" placeholder="Player Name" onChange={handleInputChange} />
                     <input type="number" name="age" placeholder="Age" onChange={handleInputChange} />
                     <input type="text" name="position" placeholder="Position" onChange={handleInputChange} />
                     <input type="number" name="clubId" placeholder="Club ID" onChange={handleInputChange} />
                     <input type="number" name="potential" placeholder="Potential" onChange={handleInputChange} />
                     <input type="number" name="overall" placeholder="Overall" onChange={handleInputChange} />
                     <input type="number" name="marketValue" placeholder="Market Value" onChange={handleInputChange} />
                     <button onClick={handleSubmit}>Submit</button>
                 </>
             );
        }
        return null;
    };
    const handleSubmit = async () => {
        try {
            setResponseMessage('Loading...');
            let apiUrl = '';
            let method = '';

            if (entityType === 'clubs') {
                if (operationType === 'insert') {
                    apiUrl = 'http://localhost:8080/api/v1/clubs';
                    method = 'post';
                }
                else if (operationType === 'update')
                {
                    apiUrl = `http://localhost:8080/api/v1/clubs/${formData.clubId}`;
                    method = 'put';
                }

                await axios({
                    method: method,
                    url: apiUrl,
                    data: formData
                });
                setResponseMessage('Request successful!');
            }
             else if (entityType === 'football-players') {
                if (operationType === 'insert') {
                    apiUrl = 'http://localhost:8080/api/v1/football-players';
                    method = 'post';
                }
                else if (operationType === 'update')
                {
                    apiUrl = `http://localhost:8080/api/v1/football-players/${formData.playerId}`;
                    method = 'put';
                }

                await axios({
                    method: method,
                    url: apiUrl,
                    data: formData
                });
                setResponseMessage('Request successful!');
            }
             else if(entityType === 'users')
            {
                if(operationType === 'delete')
                {
                     apiUrl = `http://localhost:8080/api/v1/users/${formData.userId}`;
                     method = 'delete'
                }
                 await axios({
                     method: method,
                     url: apiUrl,
                 });
                setResponseMessage('Request successful!');
            }

        }
         catch (error) {
            console.error('Error:', error);
            setResponseMessage('Request failed! ' + (error.response?.data?.message || error.message));
        }
        setFormVisible(false);
    };
    const handleDelete = async () => {
        try {
            setResponseMessage('Loading...');
            let apiUrl = '';

             if(entityType === 'clubs' ) {

                  apiUrl = `http://localhost:8080/api/v1/clubs/${formData.clubId}`;
             }
            else if (entityType === 'football-players')
             {
                 apiUrl = `http://localhost:8080/api/v1/football-players/${formData.playerId}`;
             }

           await axios.delete(apiUrl)
                .then(res => {
                    console.log(res);
                    if (res.status === 204)
                    {
                        setResponseMessage('Request successful!');
                    }
                })
        }
         catch (error) {
            console.error('Error:', error);
            setResponseMessage('Request failed! ' + (error.response?.data?.message || error.message));
        }
        setFormVisible(false);
    };
    const renderOperationOptions = () => {
        if (entityType === 'users') {
            return userOperations.map((operation) => (
                <option key={operation} value={operation}>
                    {operation}
                </option>
            ));
        } else if (entityType) {
            return otherOperations.map((operation) => (
                <option key={operation} value={operation}>
                    {operation}
                </option>
            ));
        }
        return null;
    };
    return (
        <div>
            <div>
                <label>Entity Type:</label>
                <select value={entityType} onChange={handleEntityTypeChange}>
                    <option value="">Select an Entity</option>
                    {entityTypes.map((type) => (
                        <option key={type} value={type}>
                            {type}
                        </option>
                    ))}
                </select>
            </div>

            {entityType && (
                <div>
                    <label>Operation:</label>
                    <select value={operationType} onChange={handleOperationTypeChange}>
                        <option value="">Select an Operation</option>
                        {renderOperationOptions()}
                    </select>
                </div>
            )}
            {operationType === 'delete' && entityType !== 'users' && (
                <div>
                     {entityType === 'clubs' && <input type="number" name="clubId" placeholder="Club ID" onChange={handleInputChange}/> }
                    {entityType === 'football-players' && <input type="number" name="playerId" placeholder="Player ID" onChange={handleInputChange}/> }

                    <button onClick={handleDelete}>Delete</button>
                </div>
            )}
            {operationType === 'delete' && entityType === 'users' && (
                <div>
                    <input type="number" name="userId" placeholder="User ID" onChange={handleInputChange}/>
                    <button onClick={handleDelete}>Delete</button>
                </div>
            )}
            {formVisible && (
                <div>
                    {renderFormFields()}
                </div>
            )}

            {responseMessage && (
                <p>{responseMessage}</p>
            )}
        </div>
    );
};

export default Operations;
