import React, { useState, useEffect } from "react";
import "./Players.css";

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [filters, setFilters] = useState({
    uniqueId: "",
    club: "",
    nationality: "",
    league: "",
    wonderkids: false,
    contractExpiring: false,
    freeTransfers: false,
    bestBargains: false,
    dynamicPotential: false,
    fastPaced: false,
    goalkeepers: false,
    defenders: false,
    midfielders: false,
    attackers: false,
    positions: [],
  });

  const playersPerPage = 10;
  const paginationGroupSize = 10;

  useEffect(() => {
    const data = Array.from({ length: 8000 }, (_, i) => ({
      id: i + 1,
      name: `Player ${i + 1}`,
      club: `Club ${i % 20}`,
      nationality: `Country ${i % 10}`,
      league: `League ${i % 5}`,
      position: ["GK", "DC", "MC", "ST"][i % 4],
      rating: Math.floor(Math.random() * 10) + 80,
      contractUntil: i % 7 === 0 ? "06-2024" : "06-2025",
      wage: `${Math.floor(Math.random() * 400) + 50}K p/w`,
      image: i % 7 === 0 ? null : `https://via.placeholder.com/50?text=P${i + 1}`,
    }));
    setPlayers(data);
  }, []);

  const totalPages = Math.ceil(players.length / playersPerPage);
  const indexOfLastPlayer = currentPage * playersPerPage;
  const indexOfFirstPlayer = indexOfLastPlayer - playersPerPage;

  // Filtrelenmiş oyuncular
  const filteredPlayers = players.filter((player) => {
    const {
      uniqueId,
      club,
      nationality,
      league,
      wonderkids,
      contractExpiring,
      freeTransfers,
      bestBargains,
      dynamicPotential,
      fastPaced,
      goalkeepers,
      defenders,
      midfielders,
      attackers,
      positions,
    } = filters;

    // Filtre koşulları
    if (uniqueId && !player.id.toString().includes(uniqueId)) return false;
    if (club && !player.club.toLowerCase().includes(club.toLowerCase()))
      return false;
    if (
      nationality &&
      !player.nationality.toLowerCase().includes(nationality.toLowerCase())
    )
      return false;
    if (league && !player.league.toLowerCase().includes(league.toLowerCase()))
      return false;
    if (wonderkids && player.rating < 85) return false;
    if (contractExpiring && player.contractUntil !== "06-2024") return false;
    if (freeTransfers && player.contractUntil !== "06-2024") return false;
    if (positions.length > 0 && !positions.includes(player.position))
      return false;
    return true;
  });

  const currentPlayers = filteredPlayers.slice(
    indexOfFirstPlayer,
    indexOfLastPlayer
  );

  const currentPaginationGroup = Math.floor((currentPage - 1) / paginationGroupSize);
  const startPage = currentPaginationGroup * paginationGroupSize + 1;
  const endPage = Math.min(startPage + paginationGroupSize - 1, totalPages);

  const paginate = (pageNumber) => {
    if (pageNumber > 0 && pageNumber <= totalPages) setCurrentPage(pageNumber);
  };

  const handleFilterChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFilters((prevFilters) => ({
      ...prevFilters,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const togglePositionFilter = (position) => {
    setFilters((prevFilters) => {
      const newPositions = prevFilters.positions.includes(position)
        ? prevFilters.positions.filter((pos) => pos !== position)
        : [...prevFilters.positions, position];
      return { ...prevFilters, positions: newPositions };
    });
  };

  return (
    <div className="players-page">
      <aside className="players-sidebar">
        <h2>Filters</h2>
        {/* Unique ID Filter */}
        <div className="filter-group">
          <label htmlFor="uniqueId">Unique ID</label>
          <input
            type="text"
            id="uniqueId"
            name="uniqueId"
            placeholder="Unique ID"
            value={filters.uniqueId}
            onChange={handleFilterChange}
          />
        </div>

        {/* Club Filter */}
        <div className="filter-group">
          <label htmlFor="club">Club</label>
          <input
            type="text"
            id="club"
            name="club"
            placeholder="Club"
            value={filters.club}
            onChange={handleFilterChange}
          />
        </div>

        {/* Other filters */}
        {/* ... */}
      </aside>

      <main className="players-list">
        <h1>Players</h1>
        {/* Player Table */}
        <table>
          <thead>
            <tr>
              <th>Photo</th>
              <th>Name</th>
              <th>Club</th>
              <th>Position</th>
              <th>Rating</th>
              <th>Wage</th>
              <th>Contract</th>
            </tr>
          </thead>
          <tbody>
            {currentPlayers.map((player) => (
              <tr key={player.id}>
                <td>
                  {player.image ? (
                    <img
                      src={player.image}
                      alt={player.name}
                      className="player-photo"
                    />
                  ) : (
                    <div className="placeholder-photo">No Image</div>
                  )}
                </td>
                <td>{player.name}</td>
                <td>{player.club}</td>
                <td>{player.position}</td>
                <td>{player.rating}</td>
                <td>{player.wage}</td>
                <td>{player.contractUntil}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </main>
    </div>
  );
};

export default Players;
