import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./ClubDetails.css";

const ClubDetails = () => {
  const { clubId } = useParams();
  const [club, setClub] = useState(null);
  const [players, setPlayers] = useState([]);
  const [loadingPlayers, setLoadingPlayers] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClubDetails = async () => {
      try {
        const clubResponse = await axios.get(
          "http://localhost:8080/api/v1/clubs/clubId"
        );
        setClub(clubResponse.data);

       /* const playersResponse = await axios.get(
          "http://localhost:8080/api/v1/clubs/clubId/players"
        );
        setPlayers(playersResponse.data);*/
      } catch (err) {
        console.error("Error fetching club details:", err);
        setError("Failed to load club details. Please try again later.");
      } finally {
        setLoadingPlayers(false);
      }
    };

    fetchClubDetails();
  }, [clubId]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!club) {
    return <div>No details available for this club.</div>;
  }

  return (
    <div className="club-details">
      <div className="club-info">
        <h2>{club.clubName}</h2>
        <p><strong>League:</strong> {club.leagueName}</p>
        <p><strong>Reputation:</strong> {club.reputation}</p>
        <p><strong>Manager:</strong> {club.managerName}</p>
      </div>

      <div className="club-players">
        <h3>Players</h3>
        {loadingPlayers ? (
          <p>Loading players...</p>
        ) : players.length > 0 ? (
          <table className="players-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Position</th>
                <th>Age</th>
              </tr>
            </thead>
            <tbody>
              {players.map((player) => (
                <tr key={player.id}>
                  <td>{player.name}</td>
                  <td>{player.position}</td>
                  <td>{player.age}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No players available for this club.</p>
        )}
      </div>
    </div>
  );
};

export default ClubDetails;
