import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const PlayerDetails = () => {
  const { playerId } = useParams(); // URL'deki playerId parametresini alıyoruz
  const [playerDetails, setPlayerDetails] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPlayerDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/v1/football-players/${playerId}`);
        setPlayerDetails(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching player details:", error);
        setLoading(false);
      }
    };

    fetchPlayerDetails();
  }, [playerId]); // playerId değiştiğinde tekrar veri çeker

  if (loading) {
    return <div>Loading player details...</div>;
  }

  return (
    <div>
      {playerDetails ? (
        <div>
          <h1>{playerDetails.name}</h1>
          <p>Club: {playerDetails.clubName}</p>
          <p>Position: {playerDetails.position}</p>
          <p>Salary: {playerDetails.salary}</p>
          <p>CA: {playerDetails.ca}</p>
          <p>PA: {playerDetails.pa}</p>
          <p>Age: {playerDetails.age}</p>
          {/* Diğer detaylar */}
        </div>
      ) : (
        <div>Player not found.</div>
      )}
    </div>
  );
};

export default PlayerDetails;
