import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"; // Link import edin
import "./Players.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";


const Players = () => {
  const [players, setPlayers] = useState([]); // Tüm oyuncular
  const [currentPage, setCurrentPage] = useState(1); // Geçerli sayfa
  const [loading, setLoading] = useState(true); // Yüklenme durumu
  const [favorites, setFavorites] = useState([]); // Favoriler
  const [hasMore, setHasMore] = useState(true); 

  const navigate = useNavigate();
  const playersPerLoad = 1000;


  const playersPerPage = 30; // Her sayfada 30 oyuncu gösterilecek
  const paginationGroupSize = 10; // Pagination grubu

  const taken = localStorage.getItem("userId");
  //const userId=Number(localStorage.getItem("userId"));
   // İlk oyuncuları yükle
   useEffect(() => {
    fetchPlayers(0); // İlk sayfa (offset 0) yüklenir
  }, []);
    // Oyuncuları backend'den çekmek (load more ıle alkalı cekme)
    const fetchPlayers = async (offset) => {
      try {
        setLoading(true);
        const response = await axios.get(
          `http://localhost:8080/api/v1/football-players?offset=${offset}&limit=${playersPerLoad}`
        );
        const transformedData = response.data.map((player) => ({
          id: player.playerId,
          name: player.name,
          club: player.clubName,
          position: player.positions,
          salary: player.salary,
          ca: player.ca,
          pa: player.pa,
          age: player.age,
          clubId: player.clubId,
        }));
  
        // Yeni gelen oyuncuları mevcut oyuncular listesine ekle
        setPlayers((prevPlayers) => [...prevPlayers, ...transformedData]);
  
        // Eğer dönen veri boşsa, daha fazla veri olmadığını belirt
        if (transformedData.length < playersPerLoad) {
          setHasMore(false);
        }
      } catch (error) {
        console.error("Error fetching players:", error);
      } finally {
        setLoading(false);
      }
    };

  /*useEffect(() => {
     const fetchPlayers = async () => {
       if (players.length === 0) { // Eğer daha önce veri çekilmediyse
         try {
           setLoading(true);
           const response = await axios.get(
             "http://localhost:8080/api/v1/football-players"
           );
           
           const transformedData = response.data.map((player) => ({
             id: player.playerId,
             name: player.name,
             club: player.clubName,
             position: player.positions,
             salary: player.salary,
             ca: player.ca,
             pa: player.pa,
             age: player.age,
             clubId:player.clubId,
             
           }));
 
           setPlayers(transformedData); // Veriyi kaydediyoruz
           setLoading(false);
         } catch (error) {
           console.error("Error fetching players:", error);
           setLoading(false);
         }
       }
     };
 
     fetchPlayers();
   }, [players.length]);*/
  /*useEffect(() => {
    const storedPlayers = localStorage.getItem("players");
    if (storedPlayers) {
      setPlayers(JSON.parse(storedPlayers));
      setLoading(false);
    } else {
      const fetchPlayers = async () => {
        setLoading(true);
        const response = await axios.get("http://localhost:8080/api/v1/football-players");
        const transformedData = response.data.map((player) => ({
          id: player.playerId,
          name: player.name,
