import React, { useState } from 'react';

const Operations = () => {
  const [entityType, setEntityType] = useState('');
  const [operationType, setOperationType] = useState('');

  const entityTypes = ['users', 'clubs', 'football-players'];
  const userOperations = ['delete'];
  const otherOperations = ['insert', 'delete', 'update'];

  const handleEntityTypeChange = (event) => {
    const selectedType = event.target.value;
    setEntityType(selectedType);
    setOperationType(''); // Reset operation when entity changes
  };

  const handleOperationTypeChange = (event) => {
    setOperationType(event.target.value);
  };

  const renderOperationOptions = () => {
    if (entityType === 'users') {
      return userOperations.map((operation) => (
        <option key={operation} value={operation}>
          {operation}
        </option>
      ));
    } else if (entityType) {
      return otherOperations.map((operation) => (
        <option key={operation} value={operation}>
          {operation}
        </option>
      ));
    }
      return null;
  };

  return (
    <div>
      <div>
        <label>Entity Type:</label>
        <select value={entityType} onChange={handleEntityTypeChange}>
          <option value="">Select an Entity</option>
          {entityTypes.map((type) => (
            <option key={type} value={type}>
              {type}
            </option>
          ))}
        </select>
      </div>

      {entityType && (
        <div>
          <label>Operation:</label>
          <select value={operationType} onChange={handleOperationTypeChange}>
              <option value="">Select an Operation</option>
            {renderOperationOptions()}
          </select>
        </div>
      )}

      {entityType && operationType && (
        <div>
          <p>
            Selected Entity: {entityType}, Selected Operation: {operationType}
          </p>
        </div>
      )}
    </div>
  );
};

export default Operations;
