       const fetchPlayers = async () => {
         setLoading(true);
         const response = await axios.get("http://localhost:8080/api/v1/football-players");
         const transformedData = response.data.map((player) => ({
           id: player.playerId,
           name: player.name,
           club: player.clubName,
           position: player.positions,
           salary: player.salary,
           ca: player.ca,
           pa: player.pa,
           age: player.age,
           clubId: player.clubId,
 
         }));
         localStorage.setItem("players", JSON.stringify(transformedData));
         setPlayers(transformedData);
         setLoading(false);
       };
 
       fetchPlayers();
     }
   }, []);*/
  const fetchPlayers = async (page) => {
    try {
      const response = await axios.get(`http://localhost:8080/api/v1/football-players?page=${page}&size=1000`);
      const transformedData = response.data.map((player) => ({
        id: player.playerId,
        name: player.name,
        club: player.clubName,
        position: player.positions,
        salary: player.salary,
        ca: player.ca,
        pa: player.pa,
        age: player.age,
        clubId: player.clubId,
      }));
      setPlayers((prevPlayers) => [...prevPlayers, ...transformedData]);
    } catch (error) {
      console.error("Error fetching players:", error);
    }
  };

  const handleLoadMore = () => {
    const nextPage = Math.ceil(players.length / 1000) + 1;
    fetchPlayers(nextPage);
  };




  const indexOfLastPlayer = currentPage * playersPerPage;
  const indexOfFirstPlayer = indexOfLastPlayer - playersPerPage;
  const currentPlayers = players.slice(indexOfFirstPlayer, indexOfLastPlayer);


