import React, { useState, useEffect } from "react";
import "./Players.css";
import axios from "axios";

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);
  const [favorites, setFavorites] = useState([]);

  const playersPerPage = 50;
  const paginationGroupSize = 10;


  useEffect(() => {
    // Veritabanından oyuncu verilerini çekiyoruz
    const fetchPlayers = async () => {
      try {
        setLoading(true);
        const response = await axios.get("http://localhost:8080/api/v1/football-players") // Backend'de pagination endpoint
        //setPlayers(response.data.players); // players array'i backend'den gelen veri
        const transformedData = response.data.map((player) => ({
          id: player.id,
          name: player.name,
          club:player.clubName,
          position:player.positions.split(" ").join(","),
          salary: player.salary,
          ca: player.ca,
          pa: player.pa,
          age:player.age
          
        }));
        setPlayers(transformedData);
        
        setLoading(false);
      } catch (error) {
        console.error("Error fetching players:", error);
        setLoading(false);
      }
    };

    fetchPlayers();
  }, [currentPage]);
 
  
 
  //Favourites
  const handleAddToFavorites = (player) => {
    if (!favorites.some((fav) => fav.id === player.id)) {
      setFavorites([...favorites, player]); // add to favourites
    }
  };
  

  const totalPages = Math.ceil(8000 / playersPerPage); // Toplam sayfa sayısı

  // Sayfa değiştirildiğinde yapılacaklar
  const paginate = (pageNumber) => {
    if (pageNumber > 0 && pageNumber <= totalPages) setCurrentPage(pageNumber);
  };

  const currentPaginationGroup = Math.floor((currentPage - 1) / paginationGroupSize);
  const startPage = currentPaginationGroup * paginationGroupSize + 1;
  const endPage = Math.min(startPage + paginationGroupSize - 1, totalPages);

  const goToPreviousGroup = () => {
    const previousGroupStart = startPage - paginationGroupSize;
    if (previousGroupStart > 0) {
      setCurrentPage(previousGroupStart);
    }
  };

  const goToNextGroup = () => {
    const nextGroupStart = startPage + paginationGroupSize;
    if (nextGroupStart <= totalPages) {
      setCurrentPage(nextGroupStart);
    }
  };

  if (loading) {
    return <div className="loading">Loading players...</div>;
  }

  return (
    <div className="players-page">
      <aside className="players-sidebar">
        <h2>Filters</h2>
        {/* Unique ID Filter */}
        <div className="filter-group">
          <label htmlFor="uniqueId">Unique ID</label>
          <input type="text" id="uniqueId" placeholder="Unique ID" />
        </div>

        {/* Club Filter */}
        <div className="filter-group">
          <label htmlFor="club">Club</label>
          <input type="text" id="club" placeholder="Club" />
        </div>

        {/* Nationality Filter */}
        <div className="filter-group">
          <label htmlFor="nationality">Nationality</label>
          <input type="text" id="nationality" placeholder="Nationality" />
        </div>

        {/* League Filter */}
        <div className="filter-group">
          <label htmlFor="league">League</label>
          <input type="text" id="league" placeholder="League" />
        </div>

        
        {/* Line */}
        <div className="section-title">
          <strong>Line</strong></div>
        <div className="checkbox-group">

          <tr>
            <label>
              <input type="checkbox" /> Goalkeepers
            </label>
          </tr>

          <tr>
            <label>
              <input type="checkbox" /> Defenders
            </label>

          </tr>

          <tr>
            <label>
              <input type="checkbox" /> Midfielders
            </label>
          </tr>
          <tr>
            <label>
              <input type="checkbox" /> Attackers
            </label>

          </tr>
        </div>
        <br></br>
        {/* Position */}
        <div className="section-title"><strong>Position</strong></div>
        <div className="checkbox-group">
          <tr>
            <label>
              <input type="checkbox" /> GK
            </label>
          </tr>
          <tr>
            <label>
              <input type="checkbox" /> DL
            </label>
          </tr>
          <tr>
            <label>
              <input type="checkbox" /> DC
            </label>
          </tr>
          <tr>
            <label>
              <input type="checkbox" /> DR
            </label>
          </tr>
          <tr>
            <label>
              <input type="checkbox" /> WBL
            </label>
          </tr>
          <tr>
            <label>
              <input type="checkbox" /> WBR
            </label>
          </tr>
          <tr>
            <label>
              <input type="checkbox" /> WBR
            </label>
          </tr>
          <tr>
            <label>
              <input type="checkbox" /> ML
            </label>
          </tr>
          <tr>
            <label>
              <input type="checkbox" /> MC
            </label>
          </tr>
          <tr>
            <label>
              <input type="checkbox" /> MR
            </label>
          </tr>
        </div>
      </aside>


      <main className="players-list">
        <h1>Players</h1>
        <table>
          <thead>
            <tr>
              <th>Add Favourites</th>
              <th>Name</th>
              <th>Club</th>
              <th>Position</th>
              <th>Salary</th>
              <th>AC</th>
              <th>PA</th>
              <th>Age</th>
            </tr>
          </thead>
          <tbody>
            {players.map((player) => (
              <tr key={player.id}>
                <td>
                  <button
                    className="favorite-btn"
                    onClick={() => handleAddToFavorites(player)}
                  >
                    ⭐
                  </button>
                </td>
                <td>{player.name}</td>
                <td>{player.club}</td>
                <td>{player.position}</td>
                <td>{player.salary}</td>
                <td>{player.ca}</td>
                <td>{player.pa}</td>
                <td>{player.age}</td>
              </tr>
            ))}
          </tbody>

        </table>

        <div className="pagination">
          <button
            className="pagination-btn"
            onClick={goToPreviousGroup}
            disabled={currentPage <= paginationGroupSize}
          >
            &lt;&lt;
          </button>

          {Array.from({ length: endPage - startPage + 1 }, (_, i) => {
            const pageNumber = startPage + i;
            return (
              <button
                key={pageNumber}
                className={`pagination-btn ${currentPage === pageNumber ? "active" : ""
                  }`}
                onClick={() => paginate(pageNumber)}
              >
                {pageNumber}
              </button>
            );
          })}

          <button
            className="pagination-btn"
            onClick={goToNextGroup}
            disabled={startPage + paginationGroupSize > totalPages}
          >
            &gt;&gt;
          </button>
        </div>
      </main>
    </div>
  );
};

export default Players;
