import React, { useState, useEffect } from "react";
import "./Players.css";

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [filteredPlayers, setFilteredPlayers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [filters, setFilters] = useState({
    position: [],
  });

  const playersPerPage = 10;
  const paginationGroupSize = 10;

  useEffect(() => {
    // Veri oluşturma
    const data = Array.from({ length: 8000 }, (_, i) => ({
      id: i + 1,
      name: `Player ${i + 1}`,
      club: `Club ${i % 20}`,
      nationality: `Country ${i % 10}`,
      position: ["GK", "DC", "MC", "ST"][i % 4],
      rating: Math.floor(Math.random() * 10) + 80,
      contractUntil: "06-2025",
      wage: `${Math.floor(Math.random() * 400) + 50}K p/w`,
      image: i % 7 === 0 ? null : `https://via.placeholder.com/50?text=P${i + 1}`,
    }));
    setPlayers(data);
    setFilteredPlayers(data); // Başlangıçta tüm oyuncular gösterilir
  }, []);

  const totalPages = Math.ceil(filteredPlayers.length / playersPerPage);
  const indexOfLastPlayer = currentPage * playersPerPage;
  const indexOfFirstPlayer = indexOfLastPlayer - playersPerPage;
  const currentPlayers = filteredPlayers.slice(
    indexOfFirstPlayer,
    indexOfLastPlayer
  );

  const currentPaginationGroup = Math.floor((currentPage - 1) / paginationGroupSize);
  const startPage = currentPaginationGroup * paginationGroupSize + 1;
  const endPage = Math.min(startPage + paginationGroupSize - 1, totalPages);

  const paginate = (pageNumber) => {
    if (pageNumber > 0 && pageNumber <= totalPages) setCurrentPage(pageNumber);
  };

  const goToPreviousGroup = () => {
    const previousGroupStart = startPage - paginationGroupSize;
    if (previousGroupStart > 0) {
      setCurrentPage(previousGroupStart);
    } else {
      setCurrentPage(1);
    }
  };

  const goToNextGroup = () => {
    const nextGroupStart = startPage + paginationGroupSize;
    if (nextGroupStart <= totalPages) {
      setCurrentPage(nextGroupStart);
    } else {
      setCurrentPage(totalPages);
    }
  };

  // Filtreleme için state'i güncelle
  const handleFilterChange = (filterType, value) => {
    setFilters((prevFilters) => {
      const newFilters = { ...prevFilters };
      if (newFilters[filterType].includes(value)) {
        newFilters[filterType] = newFilters[filterType].filter((item) => item !== value);
      } else {
        newFilters[filterType].push(value);
      }
      return newFilters;
    });
  };

  // Filtreleri uygula
  useEffect(() => {
    let filtered = players;

    // Pozisyona göre filtreleme
    if (filters.position.length > 0) {
      filtered = filtered.filter((player) => filters.position.includes(player.position));
    }

    setFilteredPlayers(filtered);
    setCurrentPage(1); // Yeni filtre uygulandığında sayfa 1'e dön
  }, [filters, players]);

  return (
    <div className="players-page">
      <aside className="players-sidebar">
        <h2>Filters</h2>

        {/* Position Filter */}
        <div className="section-title">
          <strong>Position</strong>
        </div>
        <div className="checkbox-group">
          {["GK", "DL", "DC", "DR", "MC", "ST"].map((position) => (
            <label key={position}>
              <input
                type="checkbox"
                value={position}
                onChange={(e) => handleFilterChange("position", e.target.value)}
              />
              {position}
            </label>
          ))}
        </div>
      </aside>

      <main className="players-list">
        <h1>Players</h1>
        <table>
          <thead>
            <tr>
              <th>Photo</th>
              <th>Name</th>
              <th>Club</th>
              <th>Position</th>
              <th>Rating</th>
              <th>Wage</th>
              <th>Contract</th>
            </tr>
          </thead>
          <tbody>
            {currentPlayers.map((player) => (
              <tr key={player.id}>
                <td>
                  {player.image ? (
                    <img
                      src={player.image}
                      alt={player.name}
                      className="player-photo"
                    />
                  ) : (
                    <div className="placeholder-photo">No Image</div>
                  )}
                </td>
                <td>{player.name}</td>
                <td>{player.club}</td>
                <td>{player.position}</td>
                <td>{player.rating}</td>
                <td>{player.wage}</td>
                <td>{player.contractUntil}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="pagination">
          <button
            className="pagination-btn"
            onClick={goToPreviousGroup}
            disabled={currentPage <= paginationGroupSize}
          >
            &lt;&lt;
          </button>

          {Array.from({ length: endPage - startPage + 1 }, (_, i) => {
            const pageNumber = startPage + i;
            return (
              <button
                key={pageNumber}
                className={`pagination-btn ${
                  currentPage === pageNumber ? "active" : ""
                }`}
                onClick={() => paginate(pageNumber)}
              >
                {pageNumber}
              </button>
            );
          })}

          <button
            className="pagination-btn"
            onClick={goToNextGroup}
            disabled={startPage + paginationGroupSize > totalPages}
          >
            &gt;&gt;
          </button>
        </div>
      </main>
    </div>
  );
};

export default Players;
