import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./ClubDetails.css"; // CSS dosyası
import axios from "axios";

const ClubDetails = () => {
  const { id } = useParams(); // URL'den takım id'sini al
  const [club, setClub] = useState(null);
  const [players, setPlayers] = useState([]);

  /*useEffect(() => {
    // Örnek veriler
    const mockClub = {
      id,
      name: `Club ${id}`,
      league: `League ${id % 5 + 1}`,
      nation: `Country ${id % 10 + 1}`,
      city: `City ${id % 10 + 1}`,
      manager: `Manager ${id}`,
      established: 1900 + (id % 120),
    };

    const mockPlayers = Array.from({ length: 20 }, (_, i) => ({
      id: i + 1,
      name: `Player ${i + 1}`,
      position: i % 4 === 0 ? "Goalkeeper" : i % 3 === 0 ? "Defender" : "Midfielder",
      age: 18 + (i % 15),
    }));

    setClub(mockClub);
    setPlayers(mockPlayers);
  }, [id]);*/
  useEffect(() => {
    const fetchClubs = async () => {
      try {
        // API'ye GET isteği gönder
        const response = await axios.get("http://localhost:8080/api/v1/clubs");
  
        // Gelen veriyi frontend'deki yapıya uygun hale getir
        const transformedData = response.data.map((club) => ({
          id: club.id,
          name: club.clubName,
          league: club.leagueName,
          nation: club.nation || "Unknown Nation", // Eğer nation verisi eksikse bir varsayılan değer ata
          manager: club.managerName,
        }));
  
        // State'i güncelle
        setClub(transformedData);
      } catch (error) {
        console.error("Error fetching clubs:", error);
      }
    };
  
    fetchClubs();
  }, []); // Component ilk render olduğunda çalışır
  

  if (!club) return <div>Loading...</div>;

  return (
    <div className="club-details-container">
      {/* Kulüp Bilgileri */}
      <aside className="club-info">
        <h3>{club.name}</h3>
        <p><strong>League:</strong> {club.league}</p>
        <p><strong>Nation:</strong> {club.nation}</p>
        <p><strong>City:</strong> {club.city}</p>
        <p><strong>Manager:</strong> {club.manager}</p>
        <p><strong>Established:</strong> {club.established}</p>
      </aside>

      {/* Oyuncu Listesi */}
      <div className="player-list">
        <h3>Players</h3>
        <table className="player-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Position</th>
              <th>Age</th>
            </tr>
          </thead>
          <tbody>
            {players.map((player) => (
              <tr key={player.id}>
                <td>{player.name}</td>
                <td>{player.position}</td>
                <td>{player.age}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ClubDetails;
