import React, { useState, useEffect } from "react";
import "./Players.css";

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const playersPerPage = 10;
  const paginationGroupSize = 10;

  useEffect(() => {
    const data = Array.from({ length: 8000 }, (_, i) => ({
      id: i + 1,
      name: `Player ${i + 1}`,
      club: `Club ${i % 20}`,
      nationality: `Country ${i % 10}`,
      position: ["GK", "DC", "MC", "ST"][i % 4],
      rating: Math.floor(Math.random() * 10) + 80,
      contractUntil: "06-2025",
      wage: `${Math.floor(Math.random() * 400) + 50}K p/w`,
      image: i % 7 === 0 ? null : `https://via.placeholder.com/50?text=P${i + 1}`,
    }));
    setPlayers(data);
  }, []);

  const totalPages = Math.ceil(players.length / playersPerPage);
  const indexOfLastPlayer = currentPage * playersPerPage;
  const indexOfFirstPlayer = indexOfLastPlayer - playersPerPage;
  const currentPlayers = players.slice(indexOfFirstPlayer, indexOfLastPlayer);

  // Şu anki sayfa grubunun başlangıç ve bitiş indekslerini hesapla
  const currentPaginationGroup = Math.floor((currentPage - 1) / paginationGroupSize);
  const startPage = currentPaginationGroup * paginationGroupSize + 1;
  const endPage = Math.min(startPage + paginationGroupSize - 1, totalPages);

  // Sayfa değiştirme işlemleri
  const paginate = (pageNumber) => {
    if (pageNumber > 0 && pageNumber <= totalPages) setCurrentPage(pageNumber);
  };

  // Grup değiştirme işlemleri (sol ve sağ tuşlar için)
  const goToPreviousGroup = () => {
    const previousGroupStart = startPage - paginationGroupSize;
    if (previousGroupStart > 0) {
      setCurrentPage(previousGroupStart);
    } else {
      setCurrentPage(1); // En başa gidiyoruz
    }
  };

  const goToNextGroup = () => {
    const nextGroupStart = startPage + paginationGroupSize;
    if (nextGroupStart <= totalPages) {
      setCurrentPage(nextGroupStart);
    } else {
      setCurrentPage(totalPages); // En sona gidiyoruz
    }
  };

  return (
    <div className="players-page">
      <aside className="players-sidebar">
        <table>
        <h2>Filters</h2>
        <td>
        <div className="filter-group">
          <label>Name</label>
          <input type="text" placeholder="Name" />
        </div>
        </td>
        <td>
        <div className="filter-group">
          <label>Club</label>
          <input type="text" placeholder="Club" />
        </div>
        </td>
        <td>
        <div className="filter-group">
          <label>Nationality</label>
          <input type="text" placeholder="Nationality" />
        </div>
        </td>
        <td>
        <div className="filter-group">
          <label>League</label>
          <input type="text" placeholder="League" />
        </div>
        </td>
        
       
       
        
        </table>
      </aside>

      <main className="players-list">
        <h1>Players</h1>
        <table>
          <thead>
            <tr>
              <th>Photo</th>
              <th>Name</th>
              <th>Club</th>
              <th>Position</th>
              <th>Rating</th>
              <th>Wage</th>
              <th>Contract</th>
            </tr>
          </thead>
          <tbody>
            {currentPlayers.map((player) => (
              <tr key={player.id}>
                <td>
                  {player.image ? (
                    <img
                      src={player.image}
                      alt={player.name}
                      className="player-photo"
                    />
                  ) : (
                    <div className="placeholder-photo">No Image</div>
                  )}
                </td>
                <td>{player.name}</td>
                <td>{player.club}</td>
                <td>{player.position}</td>
                <td>{player.rating}</td>
                <td>{player.wage}</td>
                <td>{player.contractUntil}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="pagination">
          <button
            className="pagination-btn"
            onClick={goToPreviousGroup}
            disabled={currentPage <= paginationGroupSize}
          >
            &lt;&lt;
          </button>

          {Array.from({ length: endPage - startPage + 1 }, (_, i) => {
            const pageNumber = startPage + i;
            return (
              <button
                key={pageNumber}
                className={`pagination-btn ${
                  currentPage === pageNumber ? "active" : ""
                }`}
                onClick={() => paginate(pageNumber)}
              >
                {pageNumber}
              </button>
            );
          })}

          <button
            className="pagination-btn"
            onClick={goToNextGroup}
            disabled={startPage + paginationGroupSize > totalPages}
          >
            &gt;&gt;
          </button>
        </div>
      </main>
    </div>
  );
};

export default Players;
