import React, { useEffect, useState } from "react";
import axios from "axios";

const Players = () => {
  const [allPlayers, setAllPlayers] = useState([]); // Tüm oyuncuları tutar
  const [filteredPlayers, setFilteredPlayers] = useState([]); // Filtrelenmiş oyuncuları tutar
  const [currentPage, setCurrentPage] = useState(0); // Mevcut sayfa
  const [playersPerPage, setPlayersPerPage] = useState(30); // Sayfa başına oyuncu sayısı
  const [filters, setFilters] = useState({
    minAge: null,
    maxAge: null,
    clubName: "",
    countryName: "",
    leagueName: "",
    positionIds: [],
    minMarketValue: null,
    maxMarketValue: null,
    minCa: null,
    maxCa: null,
    minPa: null,
    maxPa: null,
  });

  useEffect(() => {
    // Tüm veriyi backend'den çek
    const fetchPlayers = async () => {
      try {
        const response = await axios.get("http://localhost:8080/api/v1/football-players");
        setAllPlayers(response.data); // Tüm veriyi kaydet
        setFilteredPlayers(response.data); // Başlangıçta tüm veriyi filtrelenmiş olarak ayarla
      } catch (error) {
        console.error("Error fetching players:", error);
      }
    };

    fetchPlayers();
  }, []);

  // Filtreleri uygula
  const applyFilters = () => {
    let filtered = allPlayers;

    if (filters.minAge) {
      filtered = filtered.filter((player) => player.age >= filters.minAge);
    }
    if (filters.maxAge) {
      filtered = filtered.filter((player) => player.age <= filters.maxAge);
    }
    if (filters.clubName) {
      filtered = filtered.filter((player) =>
        player.club.toLowerCase().includes(filters.clubName.toLowerCase())
      );
    }
    if (filters.countryName) {
      filtered = filtered.filter((player) =>
        player.country.toLowerCase().includes(filters.countryName.toLowerCase())
      );
    }
    if (filters.leagueName) {
      filtered = filtered.filter((player) =>
        player.league.toLowerCase().includes(filters.leagueName.toLowerCase())
      );
    }
    if (filters.positionIds.length > 0) {
      filtered = filtered.filter((player) =>
        filters.positionIds.some((id) => player.positionIds.includes(id))
      );
    }

    setFilteredPlayers(filtered);
    setCurrentPage(0); // Yeni filtrelerde sayfayı sıfırla
  };

  useEffect(() => {
    applyFilters();
  }, [filters]); // Filtreler değiştiğinde yeniden uygula

  // Sayfa değişimi
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Filtre değişimlerini yönet
  const handleFilterChange = (key, value) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      [key]: value,
    }));
  };

  const handleCheckboxChange = (positionId) => {
    setFilters((prevFilters) => {
      const updatedPositions = prevFilters.positionIds.includes(positionId)
        ? prevFilters.positionIds.filter((id) => id !== positionId)
        : [...prevFilters.positionIds, positionId];
      return { ...prevFilters, positionIds: updatedPositions };
    });
  };

  // Sayfa başına oyuncuları hesapla
  const indexOfFirstPlayer = currentPage * playersPerPage;
  const indexOfLastPlayer = indexOfFirstPlayer + playersPerPage;
  const currentPlayers = filteredPlayers.slice(indexOfFirstPlayer, indexOfLastPlayer);

  return (
    <div className="players-page">
      <aside className="players-sidebar">
        <h2>Filters</h2>
        <div className="filter-group">
          <label>Min Age:</label>
          <input
            type="number"
            onChange={(e) => handleFilterChange("minAge", e.target.value || null)}
          />
        </div>
        <div className="filter-group">
          <label>Max Age:</label>
          <input
            type="number"
            onChange={(e) => handleFilterChange("maxAge", e.target.value || null)}
          />
        </div>
        <div className="filter-group">
          <label>Club Name:</label>
          <input
            type="text"
            onChange={(e) => handleFilterChange("clubName", e.target.value)}
          />
        </div>
        <div className="filter-group">
          <label>Position:</label>
          <label>
            <input
              type="checkbox"
              onChange={() => handleCheckboxChange(1)} // Örnek pozisyonId
            />
            GK
          </label>
          {/* Daha fazla pozisyon ekleyebilirsiniz */}
        </div>
      </aside>
      <main className="players-list">
        <h1>Players</h1>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Club</th>
              <th>Position</th>
              <th>Age</th>
              {/* Diğer sütunlar */}
            </tr>
          </thead>
          <tbody>
            {currentPlayers.map((player) => (
              <tr key={player.id}>
                <td>{player.name}</td>
                <td>{player.club}</td>
                <td>{player.position.join(", ")}</td>
                <td>{player.age}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="pagination">
          <button
            onClick={() => handlePageChange(currentPage - 1)}
            disabled={currentPage === 0}
          >
            Previous
          </button>
          <span>
            Page {currentPage + 1} of {Math.ceil(filteredPlayers.length / playersPerPage)}
          </span>
          <button
            onClick={() => handlePageChange(currentPage + 1)}
            disabled={currentPage >= Math.ceil(filteredPlayers.length / playersPerPage) - 1}
          >
            Next
          </button>
        </div>
      </main>
    </div>
  );
};

export default Players;
