import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./ClubDetails.css";

const ClubDetails = () => {
  const { clubId } = useParams(); 
  const [club, setClub] = useState(null);
  const [players, setPlayers] = useState([]);
  const [managerDetails, setManagerDetails] = useState(null);
  const [showManagerDetails, setShowManagerDetails] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClubDetails = async () => {
      try {
        const clubResponse = await axios.get(`http://localhost:8080/api/v1/clubs/${clubId}`);
        setClub(clubResponse.data);

        const playerResponse = await axios.get(`http://localhost:8080/api/v1/clubs/${clubId}/players`);
        setPlayers(playerResponse.data);
      } catch (err) {
        setError("Failed to load club details. Please try again later.");
      }
    };

    fetchClubDetails();
  }, [clubId]);

  const fetchManagerDetails = async (managerName) => {
    try {
      const response = await axios.get(`http://localhost:8080/api/v1/managers/${manager_id}`);
      setManagerDetails(response.data);
    } catch (err) {
      console.error("Error fetching manager details:", err);
    }
  };

  const getReputationColor = (reputation) => {
    if (reputation >= 85) return "purple";
    if (reputation >= 70) return "blue";
    if (reputation >= 50) return "green";
    return "red";
  };

  if (error) {
    return <div>{error}</div>;
  }

  if (!club) {
    return <div>Loading club details...</div>;
  }

  return (
    <div className="club-details">
      <div className="club-info">
        <h2>{club.clubName}</h2>
        <p><strong>Foundation Date:</strong> {club.foundationDate}</p>
        <hr />
        <p><strong>League:</strong> {club.leagueName}</p>
        <hr />
        <p>
          <strong>Reputation:</strong>{" "}
          <span style={{ color: getReputationColor(club.reputation) }}>
            {club.reputation}
          </span>
        </p>
        <hr />
        <p><strong>President:</strong> {club.presidentName}</p>
        <hr />
        <p>
          <strong>Manager:</strong>{" "}
          <span
            className="manager-name"
            onMouseEnter={() => {
              fetchManagerDetails(club.managerName);
              setShowManagerDetails(true);
            }}
            onMouseLeave={() => setShowManagerDetails(false)}
          >
            {club.managerName}
          </span>
        </p>
        {showManagerDetails && managerDetails && (
          <div className="manager-popup">
            <h4>{managerDetails.managerName}</h4>
            <p><strong>Birth Date:</strong> {managerDetails.birthDate}</p>
            <p><strong>Attacking:</strong> {managerDetails.attacking}</p>
            <p><strong>Defending:</strong> {managerDetails.defending}</p>
            <p><strong>Fitness:</strong> {managerDetails.fitness}</p>
            <p><strong>Mental:</strong> {managerDetails.mental}</p>
            <p><strong>Tactical:</strong> {managerDetails.tactical}</p>
            <p><strong>Technical:</strong> {managerDetails.technical}</p>
            <p><strong>Working With Young Stars:</strong> {managerDetails.workingWithYoungStars}</p>
            <p><strong>Club:</strong> {managerDetails.clubName}</p>
          </div>
        )}
      </div>

      <div className="club-players">
        <h3>Players</h3>
        <table className="players-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Position</th>
              <th>Age</th>
              <th>Salary</th>
            </tr>
          </thead>
          <tbody>
            {players.map((player) => (
              <tr key={player.id}>
                <td>{player.name}</td>
                <td>{Array.isArray(player.positions) ? player.positions.join(", ") : player.positions}</td>
                <td>{player.age}</td>
                <td>{player.salary}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ClubDetails;
