import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./ClubDetails.css"; // CSS dosyası
import axios from "axios";

const ClubDetails = () => {
  const { id } = useParams(); // URL'den takım id'sini al
  const [club, setClub] = useState(null);
  const [players, setPlayers] = useState([]);



  useEffect(() => {
    const fetchClubDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/v1/clubs/${id}`);
        setClub(response.data); // API'den gelen kulüp bilgisi
        // Eğer oyuncu bilgilerini başka bir endpoint'ten alıyorsan, onu da burada çağırabilirsiniz
      } catch (error) {
        console.error("Error fetching club details:", error);
      }
    };
  
    fetchClubDetails();
  }, [id]);
  
 
  

  if (!club) return <div>Loading...</div>;

  return (
    <div className="club-details-container">
      {/* Kulüp Bilgileri */}
      <aside className="club-info">
        <h3>{club.name}</h3>
        <p><strong>League:</strong> {club.league}</p>
        <p><strong>Nation:</strong> {club.nation}</p>
        <p><strong>City:</strong> {club.city}</p>
        <p><strong>Manager:</strong> {club.manager}</p>
        <p><strong>Established:</strong> {club.established}</p>
      </aside>

      {/* Oyuncu Listesi */}
      <div className="player-list">
        <h3>Players</h3>
        <table className="player-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Position</th>
              <th>Age</th>
            </tr>
          </thead>
          <tbody>
            {players.map((player) => (
              <tr key={player.id}>
                <td>{player.name}</td>
                <td>{player.position}</td>
                <td>{player.age}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ClubDetails;
