import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Players.css";
import axios from "axios";

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);
  const [favorites, setFavorites] = useState([]);
  const [hasMore, setHasMore] = useState(true); // Daha fazla oyuncu olup olmadığını tutacak.

  const navigate = useNavigate();

  const playersPerPage = 30;
  const taken = localStorage.getItem("userId");

    const fetchPlayers = async (page) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8080/api/v1/football-players?page=${page}&size=${playersPerPage}`
      );

      const transformedData = response.data.content.map((player) => ({
        id: player.playerId,
        name: player.name,
        club: player.clubName,
        position: player.positions,
        salary: player.salary,
        ca: player.ca,
        pa: player.pa,
        age: player.age,
        clubId: player.clubId,
      }));
        
         const hasMoreData = !response.data.last;
         setHasMore(hasMoreData)

      // Eğer ilk sayfa yükleniyorsa, mevcut verileri temizle. Yoksa ekleme yap.
      setPlayers((prevPlayers) => (page === 1 ? transformedData : [...prevPlayers, ...transformedData]));
    } catch (error) {
      console.error("Error fetching players:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
      fetchPlayers(1);
  }, []); // İlk yüklemede ilk sayfayı getir

  const handleLoadMore = () => {
    if (hasMore && !loading) {
         fetchPlayers(currentPage + 1)
        setCurrentPage(currentPage + 1);
      }
  };


  const handleAddToFavorites = async (player) => {
    if (taken === null) {
      navigate("/login");
      return;
    }

    try {
      const userId = Number(localStorage.getItem("userId"));

      if (!favorites.some((fav) => fav.id === player.id)) {
        setFavorites([...favorites, player]);

        const response = await fetch(
          `http://localhost:8080/api/v1/favourites?userId=${userId}&playerId=${player.id}`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
          }
        );

        if (response.ok) {
          console.log("Favoriye ekleme başarılı!");
        } else {
          console.error("Favoriye ekleme hatası:", await response.text());
        }
      }
    } catch (error) {
      console.error("Favoriye eklerken bir hata oluştu:", error);
    }
  };

  if (loading && players.length === 0) {
    return <div className="loading">Loading players...</div>;
  }

  return (
    <div className="players-page">
        <aside className="players-sidebar">
            <h2 style={{ color: "black" }}>Filters</h2>

            <div className="filter-group">
            <label htmlFor="name">Name</label>
            <input type="text" id="name" placeholder="Name" />
            </div>


            <div className="filter-group">
            <label htmlFor="club">Club</label>
            <input type="text" id="club" placeholder="Club" />
            </div>


            <div className="filter-group">
            <label htmlFor="nationality">Nationality</label>
            <input type="text" id="nationality" placeholder="Nationality" />
            </div>

            <div className="filter-group">
            <label htmlFor="league">League</label>
            <input type="text" id="league" placeholder="League" />
            </div>





            <div className="section-title"><strong>Position</strong></div>
            <div className="checkbox-group">
            <tr>
                <label>
                <input type="checkbox" /> GK
                </label>
            </tr>
            <tr>
                <label>
                <input type="checkbox" /> DL
                </label>
            </tr>
            <tr>
                <label>
                <input type="checkbox" /> DC
                </label>
            </tr>
            <tr>
                <label>
                <input type="checkbox" /> DR
                </label>
            </tr>
            <tr>
                <label>
                <input type="checkbox" /> WBL
                </label>
            </tr>
            <tr>
                <label>
                <input type="checkbox" /> WBR
                </label>
            </tr>
            <tr>
                <label>
                <input type="checkbox" /> WBR
                </label>
            </tr>
            <tr>
                <label>
                <input type="checkbox" /> ML
                </label>
            </tr>
            <tr>
                <label>
                <input type="checkbox" /> MC
                </label>
            </tr>
            <tr>
                <label>
                <input type="checkbox" /> MR
                </label>
            </tr>
            </div>
        </aside>
      <main className="players-list">
        <h1>Players</h1>
        <table>
          <thead>
            <tr>
              <th>Add Favourites</th>
              <th>Name</th>
              <th>Club</th>
              <th>Position</th>
              <th>Salary</th>
              <th>CA</th>
              <th>PA</th>
              <th>Age</th>
            </tr>
          </thead>
          <tbody>
            {players.map((player) => (
              <tr key={player.id}>
                <td>
                  <button
                    className="favorite-btn"
                    onClick={() => handleAddToFavorites(player)}
                  >
                    ⭐
                  </button>
                </td>
                <td>
                  <Link to={`/players/${player.id}`} className="player-link">
                    {player.name}
                  </Link>
                </td>
                <td>
                  {player.clubId ? (
                    <Link to={`/clubs/${player.clubId}`} className="club-link">
                      {player.club}
                    </Link>
                  ) : (
                    <span>No Club</span>
                  )}
                </td>
                <td>
                  {Array.isArray(player.position)
                    ? player.position.join(", ")
                    : player.position}
                </td>
                <td>{player.salary}€</td>
                <td>{player.ca}</td>
                <td>{player.pa}</td>
                <td>{player.age}</td>
              </tr>
            ))}
          </tbody>
        </table>
          {hasMore && (
            <button className="load-more-btn" onClick={handleLoadMore} disabled={loading} >
              {loading ? "Loading..." : "Load More"}
            </button>
          )}
      </main>
    </div>
  );
};

export default Players;
