const Favourites = ({ favorites}) => {
  return (
    <div className="favourites-page">
      <h1>Favourites</h1>
      {favorites.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Photo</th>
              <th>Name</th>
              <th>Club</th>
              <th>Position</th>
            </tr>
          </thead>
          <tbody>
            {favorites.map((player) => (
              <tr key={player.id}>
                <td>
                  {player.image ? (
                    <img
                      src={player.image}
                      alt={player.name}
                      className="player-photo"
                    />
                  ) : (
                    <div className="placeholder-photo">No Image</div>
                  )}
                </td>
                <td>{player.name}</td>
                <td>{player.club}</td>
                <td>{player.position}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No players in favourites.</p>
      )}
    </div>
  );
};
export default Favourites;
