import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Players.css";
import axios from "axios";

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState({
    name: "",
    club: "",
    nationality: "",
    league: "",
    minAge: null,
    maxAge: null,
    minMarketValue: null,
    maxMarketValue: null,
    minCa: null,
    maxCa: null,
    minPa: null,
    maxPa: null,
    positions: [],
  });

  const [positionOptions] = useState([
    { id: 1, label: "GK" },
    { id: 2, label: "DL" },
    { id: 3, label: "DC" },
    { id: 4, label: "DR" },
    { id: 5, label: "WBL" },
    { id: 6, label: "WBR" },
    { id: 7, label: "ML" },
    { id: 8, label: "MC" },
    { id: 9, label: "MR" },
  ]);

  const fetchPlayers = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:8080/api/v1/football-players/filter", {
        params: filter,
      });
      const transformedData = response.data.map((player) => ({
        id: player.playerId,
        name: player.name,
        club: player.clubName,
        position: player.positions,
        salary: player.salary,
        ca: player.ca,
        pa: player.pa,
        age: player.age,
        clubId: player.clubId,
      }));
      setPlayers(transformedData);
    } catch (error) {
      console.error("Error fetching players:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      setFilter((prev) => {
        const updatedPositions = checked
          ? [...prev.positions, parseInt(value)]
          : prev.positions.filter((id) => id !== parseInt(value));
        return { ...prev, positions: updatedPositions };
      });
    } else {
      setFilter((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const handleApplyFilters = () => {
    // Default değerler atama
    const updatedFilter = { ...filter };
    if (filter.minAge && !filter.maxAge) updatedFilter.maxAge = 100;
    if (filter.maxAge && !filter.minAge) updatedFilter.minAge = 0;
    if (filter.minMarketValue && !filter.maxMarketValue) updatedFilter.maxMarketValue = 999999999;
    if (filter.maxMarketValue && !filter.minMarketValue) updatedFilter.minMarketValue = 0;
    if (filter.minCa && !filter.maxCa) updatedFilter.maxCa = 200;
    if (filter.maxCa && !filter.minCa) updatedFilter.minCa = 0;
    if (filter.minPa && !filter.maxPa) updatedFilter.maxPa = 200;
    if (filter.maxPa && !filter.minPa) updatedFilter.minPa = 0;

    setFilter(updatedFilter);
    fetchPlayers();
  };

  useEffect(() => {
    fetchPlayers();
  }, []);

  if (loading) {
    return <div className="loading">Loading players...</div>;
  }

  return (
    <div className="players-page">
      <aside className="players-sidebar">
        <h2>Filters</h2>

        <div className="filter-group">
          <label htmlFor="name">Name</label>
          <input type="text" id="name" name="name" value={filter.name} onChange={handleFilterChange} placeholder="Name" />
        </div>

        <div className="filter-group">
          <label htmlFor="club">Club</label>
          <input type="text" id="club" name="club" value={filter.club} onChange={handleFilterChange} placeholder="Club" />
        </div>

        <div className="filter-group">
          <label htmlFor="nationality">Nationality</label>
          <input
            type="text"
            id="nationality"
            name="nationality"
            value={filter.nationality}
            onChange={handleFilterChange}
            placeholder="Nationality"
          />
        </div>

        <div className="filter-group">
          <label htmlFor="league">League</label>
          <input
            type="text"
            id="league"
            name="league"
            value={filter.league}
            onChange={handleFilterChange}
            placeholder="League"
          />
        </div>

        <div className="filter-group">
          <label>Age</label>
          <input
            type="number"
            name="minAge"
            value={filter.minAge || ""}
            onChange={handleFilterChange}
            placeholder="Min Age"
          />
          <input
            type="number"
            name="maxAge"
            value={filter.maxAge || ""}
            onChange={handleFilterChange}
            placeholder="Max Age"
          />
        </div>

        <div className="filter-group">
          <label>Market Value (€)</label>
          <input
            type="number"
            name="minMarketValue"
            value={filter.minMarketValue || ""}
            onChange={handleFilterChange}
            placeholder="Min Value"
          />
          <input
            type="number"
            name="maxMarketValue"
            value={filter.maxMarketValue || ""}
            onChange={handleFilterChange}
            placeholder="Max Value"
          />
        </div>

        <div className="filter-group">
          <label>Positions</label>
          {positionOptions.map((position) => (
            <div key={position.id}>
              <label>
                <input
                  type="checkbox"
                  value={position.id}
                  onChange={handleFilterChange}
                  checked={filter.positions.includes(position.id)}
                />
                {position.label}
              </label>
            </div>
          ))}
        </div>

        <button onClick={handleApplyFilters} className="apply-filters-btn">
          Apply Filters
        </button>
      </aside>

      <main className="players-list">
        <h1>Players</h1>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Club</th>
              <th>Position</th>
              <th>Salary</th>
              <th>CA</th>
              <th>PA</th>
              <th>Age</th>
            </tr>
          </thead>
          <tbody>
            {players.map((player) => (
              <tr key={player.id}>
                <td>
                  <Link to={`/players/${player.id}`}>{player.name}</Link>
                </td>
                <td>
                  <Link to={`/clubs/${player.clubId}`}>{player.club}</Link>
                </td>
                <td>{Array.isArray(player.position) ? player.position.join(", ") : player.position}</td>
                <td>{player.salary}€</td>
                <td>{player.ca}</td>
                <td>{player.pa}</td>
                <td>{player.age}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </main>
    </div>
  );
};

export default Players;
