import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"; // Link import edin
import "./Players.css";
import axios from "axios";

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    minAge: "",
    maxAge: "",
    countryId: "",
    clubId: "",
    leagueId: "",
    positionIds: [],
    minMarketValue: "",
    maxMarketValue: "",
    minCa: "",
    maxCa: "",
    minPa: "",
    maxPa: "",
  });

  const playersPerPage = 30;
  const paginationGroupSize = 10;

  useEffect(() => {
    fetchFilteredPlayers();
  }, []);

  const fetchFilteredPlayers = async () => {
    try {
      setLoading(true);
      const response = await axios.get("http://localhost:8080/api/v1/football-players/filter", {
        params: filters,
      });
      const transformedData = response.data.map((player) => ({
        id: player.playerId,
        name: player.name,
        club: player.clubName,
        position: player.positions,
        salary: player.salary,
        ca: player.ca,
        pa: player.pa,
        age: player.age,
        clubId: player.clubId,
      }));
      setPlayers(transformedData);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching players:", error);
      setLoading(false);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === "checkbox") {
      setFilters((prevFilters) => ({
        ...prevFilters,
        positionIds: checked
          ? [...prevFilters.positionIds, parseInt(value)]
          : prevFilters.positionIds.filter((id) => id !== parseInt(value)),
      }));
    } else {
      setFilters((prevFilters) => ({
        ...prevFilters,
        [name]: value ,
      }));
    }
  };

  const handleFilter = () => {
    setCurrentPage(1);
    fetchFilteredPlayers();
  };

  const indexOfLastPlayer = currentPage * playersPerPage;
  const indexOfFirstPlayer = indexOfLastPlayer - playersPerPage;
  const currentPlayers = players.slice(indexOfFirstPlayer, indexOfLastPlayer);

  const totalPages = Math.ceil(players.length / playersPerPage);
  const paginate = (pageNumber) => {
    if (pageNumber > 0 && pageNumber <= totalPages) setCurrentPage(pageNumber);
  };

  const currentPaginationGroup = Math.floor((currentPage - 1) / paginationGroupSize);
  const startPage = currentPaginationGroup * paginationGroupSize + 1;
  const endPage = Math.min(startPage + paginationGroupSize - 1, totalPages);

  if (loading) {
    return <div className="loading">Loading players...</div>;
  }

  return (
    <div className="players-page">
      <aside className="players-sidebar">
        <h2>Filters</h2>

        <div className="filter-group">
          <label htmlFor="minAge">Min Age</label>
          <input
            type="number"
            id="minAge"
            name="minAge"
            value={filters.minAge}
            onChange={handleFilterChange}
          />
        </div>

        <div className="filter-group">
          <label htmlFor="maxAge">Max Age</label>
          <input
            type="number"
            id="maxAge"
            name="maxAge"
            value={filters.maxAge}
            onChange={handleFilterChange}
          />
        </div>

        <div className="filter-group">
          <label htmlFor="countryId">Country ID</label>
          <input
            type="number"
            id="countryId"
            name="countryId"
            value={filters.countryId}
            onChange={handleFilterChange}
          />
        </div>

        <div className="filter-group">
          <label htmlFor="clubId">Club ID</label>
          <input
            type="number"
            id="clubId"
            name="clubId"
            value={filters.clubId}
            onChange={handleFilterChange}
          />
        </div>

        <div className="filter-group">
          <label htmlFor="leagueId">League ID</label>
          <input
            type="number"
            id="leagueId"
            name="leagueId"
            value={filters.leagueId}
            onChange={handleFilterChange}
          />
        </div>

        <div className="filter-group">
          <label htmlFor="minMarketValue">Min Market Value</label>
          <input
            type="number"
            id="minMarketValue"
            name="minMarketValue"
            value={filters.minMarketValue}
            onChange={handleFilterChange}
          />
        </div>

        <div className="filter-group">
          <label htmlFor="maxMarketValue">Max Market Value</label>
          <input
            type="number"
            id="maxMarketValue"
            name="maxMarketValue"
            value={filters.maxMarketValue}
            onChange={handleFilterChange}
          />
        </div>

        <div className="section-title"><strong>Position</strong></div>
        <div className="checkbox-group">
          {["GK", "DL", "DC", "DR", "WBL", "WBR", "ML", "MC", "MR"].map((pos, index) => (
            <label key={index}>
              <input
                type="checkbox"
                value={index + 1}
                onChange={handleFilterChange}
              />{" "}
              {pos}
            </label>
          ))}
        </div>

        <button onClick={handleFilter}>Apply Filters</button>
      </aside>

      <main className="players-list">
        <h1>Players</h1>
        <table>
          <thead>
            <tr>
              <th>Add Favourites</th>
              <th>Name</th>
              <th>Club</th>
              <th>Position</th>
              <th>Salary</th>
              <th>CA</th>
              <th>PA</th>
              <th>Age</th>
            </tr>
          </thead>

          <tbody>
            {currentPlayers.map((player) => (
              <tr key={player.id}>
                <td>
                  <button
                    className="favorite-btn"
                  >
                    ⭐
                  </button>
                </td>
                <td>
                  <Link to={`/players/${player.id}`} className="player-link">
                    {player.name}
                  </Link>
                </td>
                <td>
                  <Link to={`/clubs/${player.clubId}`} className="club-link">
                    {player.club}
                  </Link>
                </td>
                <td>{Array.isArray(player.position) ? player.position.join(", ") : player.position}</td>
                <td>{player.salary}€</td>
                <td>{player.ca}</td>
                <td>{player.pa}</td>
                <td>{player.age}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="pagination">
          {Array.from({ length: endPage - startPage + 1 }, (_, i) => {
            const pageNumber = startPage + i;
            return (
              <button
                key={pageNumber}
                className={`pagination-btn ${currentPage === pageNumber ? "active" : ""}`}
                onClick={() => paginate(pageNumber)}
              >
                {pageNumber}
              </button>
            );
          })}
        </div>
      </main>
    </div>
  );
};

export default Players;
