import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Players.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Players = () => {
  const [players, setPlayers] = useState([]);
  const [filteredPlayers, setFilteredPlayers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);
  const [favorites, setFavorites] = useState([]);

  const navigate = useNavigate();

  const [filters, setFilters] = useState({
    name: "",
    age: "",
    nationality: "",
    league: "",
    position: [],
  });

  const playersPerPage = 30;
  const paginationGroupSize = 10;

  const taken = localStorage.getItem("userId");

  useEffect(() => {
    const fetchPlayers = async () => {
      if (players.length === 0) {
        try {
          setLoading(true);
          const response = await axios.get(
            "http://localhost:8080/api/v1/football-players/free-transfers"
          );

          const transformedData = response.data.map((player) => ({
            id: player.playerId,
            name: player.name,
            club: player.clubName,
            position: player.positions,
            salary: player.salary,
            ca: player.ca,
            pa: player.pa,
            age: player.age,
            clubId: player.clubId,
          }));

          setPlayers(transformedData);
          setFilteredPlayers(transformedData);
          setLoading(false);
        } catch (error) {
          console.error("Error fetching players:", error);
          setLoading(false);
        }
      }
    };

    fetchPlayers();
  }, [players.length]);

  useEffect(() => {
    filterPlayers();
  }, [filters]);

  const filterPlayers = () => {
    const { name, club, nationality, league, position } = filters;

    let filtered = players;

    if (name) {
      filtered = filtered.filter((player) =>
        player.name.toLowerCase().includes(name.toLowerCase())
      );
    }

    if (club) {
      filtered = filtered.filter((player) =>
        player.club.toLowerCase().includes(club.toLowerCase())
      );
    }

    // Placeholder: Add actual filtering logic for nationality and league if data supports it

    if (position.length > 0) {
      filtered = filtered.filter((player) =>
        position.some((pos) => player.position.includes(pos))
      );
    }

    setFilteredPlayers(filtered);
  };

  const handleFilterChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === "checkbox") {
      setFilters((prevFilters) => {
        const updatedPositions = checked
          ? [...prevFilters.position, value]
          : prevFilters.position.filter((pos) => pos !== value);

        return { ...prevFilters, position: updatedPositions };
      });
    } else {
      setFilters((prevFilters) => ({ ...prevFilters, [name]: value }));
    }
  };

  const handleAddToFavorites = async (player) => {
    if (taken === null) {
      navigate("/login");
      return;
    }

    try {
      const userId = Number(localStorage.getItem("userId"));

      if (!favorites.some((fav) => fav.id === player.id)) {
        setFavorites([...favorites, player]);

        const response = await fetch(
          `http://localhost:8080/api/v1/favourites?userId=${userId}&playerId=${player.id}`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
          }
        );

        if (response.ok) {
          console.log("Favoriye ekleme başarılı!");
        } else {
          console.error("Favoriye ekleme hatası:", await response.text());
        }
      }
    } catch (error) {
      console.error("Favoriye eklerken bir hata oluştu:", error);
    }
  };

  const totalPages = Math.ceil(filteredPlayers.length / playersPerPage);

  const indexOfLastPlayer = currentPage * playersPerPage;
  const indexOfFirstPlayer = indexOfLastPlayer - playersPerPage;
  const currentPlayers = filteredPlayers.slice(indexOfFirstPlayer, indexOfLastPlayer);

  const paginate = (pageNumber) => {
    if (pageNumber > 0 && pageNumber <= totalPages) setCurrentPage(pageNumber);
  };

  const currentPaginationGroup = Math.floor((currentPage - 1) / paginationGroupSize);
  const startPage = currentPaginationGroup * paginationGroupSize + 1;
  const endPage = Math.min(startPage + paginationGroupSize - 1, totalPages);

  const goToPreviousGroup = () => {
    const previousGroupStart = startPage - paginationGroupSize;
    if (previousGroupStart > 0) {
      setCurrentPage(previousGroupStart);
    }
  };

  const goToNextGroup = () => {
    const nextGroupStart = startPage + paginationGroupSize;
    if (nextGroupStart <= totalPages) {
      setCurrentPage(nextGroupStart);
    }
  };

  if (loading) {
    return <div className="loading">Loading players...</div>;
  }

  return (
    <div className="players-page">
      <aside className="players-sidebar">
        <h2>Filters</h2>

        <div className="filter-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            name="name"
            placeholder="Name"
            value={filters.name}
            onChange={handleFilterChange}
          />
        </div>

        <div className="filter-group">
          <label htmlFor="age">Age</label>
          <input
            type="text"
            id="age"
            name="age"
            placeholder="Age"
            value={filters.age}
            onChange={handleFilterChange}
          />
        </div>

        {/* Nationality and League inputs can be similarly implemented */}

        <div className="section-title">
          <strong>Position</strong>
        </div>
        <div className="checkbox-group">
          {["GK", "DL", "DC", "DR", "WBL", "WBR", "ML", "MC", "MR","AML","AMC","AMR","ST"].map((pos) => (
            <label key={pos}>
              <input
                type="checkbox"
                name="position"
                value={pos}
                checked={filters.position.includes(pos)}
                onChange={handleFilterChange}
              />
              {pos}
            </label>
          ))}
        </div>
      </aside>

      <main className="players-list">
        <h1>Players</h1>
        <table>
          <thead>
            <tr>
              <th>Add Favourites</th>
              <th>Name</th>
              <th>Club</th>
              <th>Position</th>
              <th>Salary</th>
              <th>CA</th>
              <th>PA</th>
              <th>Age</th>
            </tr>
          </thead>

          <tbody>
            {currentPlayers.map((player) => (
              <tr key={player.id}>
                <td>
                  <button
                    className="favorite-btn"
                    onClick={() => handleAddToFavorites(player)}
                  >
                    ⭐
                  </button>
                </td>
                <td>
                  <Link to={`/players/${player.id}`} className="player-link">
                    {player.name}
                  </Link>
                </td>
                <td>
                  
                    {player.club}
                  
                </td>
                <td>{player.position.join(", ")}</td>
                <td>{player.salary}€</td>
                <td>{player.ca}</td>
                <td>{player.pa}</td>
                <td>{player.age}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="pagination">
          <button
            className="pagination-btn"
            onClick={goToPreviousGroup}
            disabled={currentPage <= paginationGroupSize}
          >
            &lt;&lt;
          </button>

          {Array.from({ length: endPage - startPage + 1 }, (_, i) => {
            const pageNumber = startPage + i;
            return (
              <button
                key={pageNumber}
                className={`pagination-btn ${
                  currentPage === pageNumber ? "active" : ""
                }`}
                onClick={() => paginate(pageNumber)}
              >
                {pageNumber}
              </button>
            );
          })}

          <button
            className="pagination-btn"
            onClick={goToNextGroup}
            disabled={startPage + paginationGroupSize > totalPages}
          >
            &gt;&gt;
          </button>
        </div>
      </main>
    </div>
  );
};

export default Players;
