import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Operations.css";

const Operations = () => {
    const [entityType, setEntityType] = useState(""); // İlk dropdown seçimi
    const [operation, setOperation] = useState(""); // İkinci dropdown seçimi
    const [attributes, setAttributes] = useState({});
    const [dataList, setDataList] = useState([]);
    const [message, setMessage] = useState("");
    const [editMode, setEditMode] = useState(false);  // Flag to indicate if we are editing

    // İlk dropdown seçenekleri
    const entityOptions = ["clubs", "football-players", "users"];

    // İkinci dropdown seçenekleri
    const operationOptions = entityType === "users" ? ["delete"] : ["insert", "delete", "update"];

    // İlk dropdown seçimi değiştiğinde her şeyi sıfırla
    useEffect(() => {
        setOperation("");
        setAttributes({});
        setDataList([]);
        setMessage("");
        setEditMode(false); //Reset Edit Mode when entity type changes
    }, [entityType]);

     // Operation seçiminde form resetlemesi
     useEffect(() => {
        if(operation==="insert" || operation==="update"){
        setAttributes({});
        setEditMode(false); // Reset edit mode when operation changes
    }
    }, [operation]);


    const getIdField = (entityType) => {
        if (entityType === "clubs") return "clubId";
        if (entityType === "football-players") return "playerId";
        if (entityType === "users") return "userId";
        return "id"; // Varsayılan bir fallback
    };


    // Veriyi backend'den getirme
    const fetchData = async () => {
        try {
          let response = null; // Başlangıç değeri
      
          // entityType'a göre uygun endpoint'i belirliyoruz
          if (entityType === "users") {
            response = await axios.get("http://localhost:8080/api/v1/users/non-admins");
          } else if (entityType === "clubs") {
            response = await axios.get("http://localhost:8080/api/v1/clubs");
          } else if (entityType === "football-players") {
            response = await axios.get("http://localhost:8080/api/v1/football-players");
          }
      
          if (response) {
            setDataList(response.data); // Gelen veriyi listeye ekliyoruz
            setMessage(""); // Başarılı istek sonrası mesajı temizliyoruz
          } else {
            setMessage("Invalid entity type selected.");
          }
        } catch (error) {
          setMessage("An error occurred while fetching data.");
          console.error(error);
        }
      };
      

    // Backend'e veri gönderme (Insert, Update veya Delete)
    const sendData = async (method, url, payload) => {
        try {
            if (method === "delete") {
                await axios.delete(url);
            } else {
                // Convert 'leagueName' and 'managerName' to numbers if they exist
                if (payload.leagueName) {
                  payload.leagueName = Number(payload.leagueName);
                }
                if (payload.managerName) {
                    payload.managerName = Number(payload.managerName);
                 }
                  if (payload.clubId) {
                     payload.clubId = Number(payload.clubId);
                }
                if(payload.positions){
                    payload.positions= payload.positions.split(',').map(pos => pos.trim());
                }
               if(payload.countryName){
                   payload.countryName = payload.countryName
               }
               if(payload.rentalClubName){
                   payload.rentalClubName = payload.rentalClubName
               }
               
                await axios({
                    method,
                    url,
                    data: payload,
                });
            }
            setMessage("Successfully done!");
            fetchData(); // Operasyondan sonra veriyi güncelle
            setAttributes({});
            setEditMode(false);
        } catch (error) {
            setMessage("An error occurred during the operation.");
            console.error(error);
        }
    };

      const handleEditClick = (item) => {
        setAttributes(item);
        setEditMode(true); // Edit mode aktif

    };

    // Render edilen form
    const renderForm = () => {
        if (operation === "insert" && !editMode) { // Only show insert form if not in edit mode
            const initialAttributes = {};
            if (entityType === "clubs") {
                initialAttributes.clubId = '';
                initialAttributes.clubName = '';
                initialAttributes.foundationDate = '';
                initialAttributes.leagueName = '';
                initialAttributes.reputation = '';
                initialAttributes.presidentName = '';
                initialAttributes.managerName = '';
            } else if (entityType === "football-players") {
                initialAttributes.playerId='';
                initialAttributes.name = '';
                initialAttributes.age='';
                initialAttributes.countryName = '';
                initialAttributes.clubId='';
                initialAttributes.clubName='';
                initialAttributes.positions = '';
                initialAttributes.ca= '';
                initialAttributes.pa = '';
                initialAttributes.corners = '';
                initialAttributes.crossing = '';
                initialAttributes.dribbling = '';
                initialAttributes.finishing = '';
                initialAttributes.firstTouche = '';
                initialAttributes.freeKickTaking = '';
                initialAttributes.heading = '';
                initialAttributes.longShots = '';
                initialAttributes.longThrows = '';
                initialAttributes.marking = '';
                initialAttributes.passing = '';
                initialAttributes.penaltyTaking = '';
                initialAttributes.tackling = '';
                initialAttributes.technique = '';
                initialAttributes.aggression = '';
                initialAttributes.anticipation = '';
                initialAttributes.bravery = '';
                initialAttributes.composure = '';
                initialAttributes.concentration = '';
                initialAttributes.vision = '';
                initialAttributes.decision = '';
                initialAttributes.determination = '';
                initialAttributes.flair = '';
                initialAttributes.leadership = '';
                initialAttributes.offTheBall = '';
                initialAttributes.positioning = '';
                initialAttributes.teamWork = '';
                initialAttributes.workRate = '';
                initialAttributes.acceleration = '';
                initialAttributes.agility = '';
                initialAttributes.balance = '';
                initialAttributes.pumpingReach = '';
                initialAttributes.naturalFitness = '';
                initialAttributes.pace = '';
                initialAttributes.stamina = '';
                initialAttributes.strength = '';
                initialAttributes.stability = '';
                initialAttributes.height = '';
                initialAttributes.leftFoot = '';
                initialAttributes.rightFoot = '';
                initialAttributes.marketValue = '';
                initialAttributes.salary = '';
                initialAttributes.rentalClubName = '';
                initialAttributes.addToFavourites= '';
            }

            return (
                <div>
                    <h3>Insert {entityType}</h3>
                    {/* Dinamik attribute inputları */}
                    {Object.keys(initialAttributes).map((key) => (
                        <div key={key}>
                            <label>{key}: </label>
                            <input
                                type="text"
                                value={attributes[key] || ''} // Use the value from state
                                onChange={(e) =>
                                    setAttributes({ ...attributes, [key]: e.target.value })
                                }
                            />
                        </div>
                    ))}
                    <button
                        onClick={() =>
                            sendData("POST", `http://localhost:8080/api/v1/${entityType}`, attributes)
                        }
                    >
                        Submit
                    </button>
                </div>
            );
        } else if (operation === "delete") {
            return (
                <div>
                    <h3>Delete {entityType}</h3>
                    <button onClick={fetchData}>Fetch All</button>
                    <ul>
                        {dataList.map((item, index) => {
                            const idField = getIdField(entityType); // Doğru ID alanını belirle
                            const itemId = item[idField]; // item içindeki doğru ID'yi al
                            
                            return (
                                <li key={index}>
                                    {`ID: ${itemId}, Name: ${item.userName || item.name || item.clubName}`}{" "}
                                    <button
                                        onClick={() => {
                                            if (!itemId) {
                                                alert(`Invalid ID for item: ${JSON.stringify(item)}`);
                                                return;
                                            }
                                            sendData(
                                                "delete",
                                                `http://localhost:8080/api/v1/${entityType}/${itemId}`
                                            );
                                        }}
                                    >
                                        Delete
                                    </button>
                                </li>
                            );
                        })}
                    </ul>

                </div>
            );
        } else if (operation === "update") {
            return (
              <div>
                    <h3>Update {entityType}</h3>
                    <button onClick={fetchData}>Fetch All</button>
                    <ul>
                        {dataList.map((item, index) => (
                            <li key={index}>
                                {`ID: ${item[getIdField(entityType)]}, Name: ${item.userName || item.name || item.clubName}`}
                                <button
                                    onClick={() => handleEditClick(item)}
                                >
                                    Edit
                                </button>
                            </li>
                        ))}
                    </ul>
                   {editMode && Object.keys(attributes).length > 0 && (
                        <div>
                            <h4>Edit Attributes</h4>
                            {Object.keys(attributes).map((key) => (
                                <div key={key}>
                                    <label>{key}: </label>
                                    <input
                                        type="text"
                                        value={attributes[key] || ''}
                                        onChange={(e) =>
                                            setAttributes({ ...attributes, [key]: e.target.value })
                                        }
                                    />
                                </div>
                            ))}
                            <button
                                onClick={() =>
                                    sendData(
                                        "PUT",
                                        `http://localhost:8080/api/v1/${entityType}/${attributes[getIdField(entityType)]}`,
                                        attributes
                                    )
                                }
                            >
                                Update
                            </button>
                        </div>
                    )}
                </div>
            );
        }
        return null;
    };

    return (
        <div className="operations">
            <h2>Admin Operations</h2>
            {/* İlk Dropdown */}
            <div>
                <label>Select Entity: </label>
                <select value={entityType} onChange={(e) => setEntityType(e.target.value)}>
                    <option value="" disabled>
                        -- Select --
                    </option>
                    {entityOptions.map((option) => (
                        <option key={option} value={option}>
                            {option}
                        </option>
                    ))}
                </select>
            </div>

            {/* İkinci Dropdown */}
            {entityType && (
                <div>
                    <label>Select Operation: </label>
                    <select value={operation} onChange={(e) => setOperation(e.target.value)}>
                        <option value="" disabled>
                            -- Select --
                        </option>
                        {operationOptions.map((option) => (
                            <option key={option} value={option}>
                                {option}
                            </option>
                        ))}
                    </select>
                </div>
            )}

            {/* Form veya Liste Renderlama */}
            {operation && renderForm()}

            {/* Mesaj */}
            {message && <div className="message">{message}</div>}
        </div>
    );
};

export default Operations;
